import { useState } from "react"

const Button = ({eventHandler, text}) => {
  return (
      <button onClick={eventHandler}>
        {text}
      </button>
  )
}

const Statistics = ({good, neutral, bad, all, avg, pos}) => {
  if(all === 0) {
    return (
      <div>
        <h1>Statistics</h1>
        <p>No feedback given</p>
      </div>
    )
  } else {
    return (
      <div>
        <h1>Statistics</h1>
        <p>good {good}</p>
        <p>neutral {neutral}</p>
        <p>bad {bad}</p>
        <p>all {all}</p>
        <p>average {avg}</p>
        <p>positive {pos}%</p>
      </div>
    )
  }
}

const Statisticsline = ({text, value}) => {
  return(
    <p>{text} {value}</p>
  )
}

const App = () => {
  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)
  const [all, setAll] = useState(0)

  const addOneGood = () => {
    setGood(good + 1)
    setAll(all + 1)
  }
  const addOneNeutral = () => {
    setNeutral(neutral + 1)
    setAll(all + 1)
  }
  const addOneBad = () => {
    setBad(bad + 1)
    setAll(all + 1)
  }

  let average = (good - bad)/all
  let positivePercentage = (good/all)*100

  return(
    <div>
      <h1>Give Feedback</h1>
      <Button eventHandler={addOneGood} text={'good'}/>
      <Button eventHandler={addOneNeutral} text={'neutral'}/>
      <Button eventHandler={addOneBad} text={'bad'}/>
      <Statistics good={good} bad={bad} neutral={neutral} all={all} avg={average} pos={positivePercentage}/>
    </div>
  )
}

export default App
