const Header = ({title}) => {
  return(
    <>
      <h1>{title}</h1>
    </>
  )
}

const Course = ({course}) => {
  return(
    <>
      <Header title={course.name}/>
      <Content course={course.parts}/>
    </>
  )
}

const Content = ({course}) => {
  return(
    <>
      {course.map(element => 
        <Part key={element.id} name={element.name} exercises={element.exercises}/>
        )}
    </>
  )
}

