import axios from 'axios'
import { useState, useEffect } from 'react'
import Filter from './components/Filter'
import Add from './components/Add'
import Numbers from './components/Numbers'
import contact from './services/contact'


const App = () => {
  const [persons, setPersons] = useState([])
  const [newName, setNewName] = useState('')
  const [newNumber, setNewNumber] = useState('')
  const [filter, setFilter] = useState('')

  useEffect(() => {
    contact
      .getAll()
      .then(allContacts => {
        setPersons(allContacts)
      })
  }, [])

  const addNew = (event) => {
    event.preventDefault()
    const newPerson = {
      name: newName,
      number: newNumber
    }

    const personExists = persons.some(person => person.name === newName)

    personExists
      ? alert(`${newName} is already added to the phonebook.`)
      :contact
        .create(newPerson)
        .then(created => {
          setPersons(persons.concat(created))
        })
    setNewName('')
    setNewNumber('')
  }

  const filteredPerson = persons.filter(person => 
    person.name && person.name.toLowerCase().includes(filter.toLowerCase())
  )



  return (
    <div>
      <h1>Phonebook</h1>
      <Filter persons={persons} filter={filter} setFilter={setFilter}/>
      <Add addNew={addNew} newName={newName} newNumber={newNumber} setNewName={setNewName} setNewNumber={setNewNumber}/>
      <Numbers filteredPerson={filteredPerson}/>
    </div>
  )
}

export default App
