const Notification = ({ message, isTrue }) => {

    let cName = ''

    if (message === null) {
      return null
    }
    if (isTrue) {
        cName = 'error' 
    } else{
        cName = 'success'
    }
  
    return (
      <div className={cName}>
        {message}
      </div>
    )
  }

export default Notification
